import { Store } from "redux";
import { StoreStateType } from './../type/Type';

export class Mole {
    private _store!: Store;

    /**
     * Store 초기화.
     * @param inStore Store
     */
    public init(inStore: Store) {
        this._store = inStore;
    }

    public get store() {
        return this._store;
    }

    public set store(inStore: Store) {
        this.store = inStore;
    }

    public get state(): StoreStateType {
        return this.store.getState()
    }
}

export default new Mole();
