import React, { useEffect, useState } from 'react';

const ProgressBar = () => {
    const [ time, setTime ] = useState(60); 
    useEffect(() => {
        const interval = setInterval(() => {
            setTime(time => {
                const newTime = time - 1;

                if (newTime === 0) {
                    clearInterval(interval);
                }

                return newTime;
            })
        }, 1000);
    }, [])

    return (
        <progress value={time}></progress>
    )
}

export default ProgressBar;
