import { motion } from "framer-motion";



const Game = () => {
    const { screenData, score } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            score: state.data.score
        }
    });


    return (
        <motion.div
   /* 2. 원하는 애니메이션으로 jsx를 감싸준다 */
        initial={{opacity: 0}}
        animate={{opacity: 1}}
        exit={{opacity: 0}}
        >
        <ProgressBar />

        </motion.div>
    )
}

export default Game;
