import mole from "./Mole";

let score = 0;

export default class Module {
    /** 행x열의 값 */
    getRowXColumnsCount = () => {
        const screenData = mole.getState('screenData');
        return screenData.row * screenData.column;
    }

    /** 두더지가 출현할 Elment Index */
    getRandomHoles = () => {
        const screenData = mole.getState('screenData');
        const result = []

        for (let i = 0; i<screenData.count; i++) {
            result.push(mole.utils.randomNum(this.getRowXColumnsCount()));
        }

        const set = new Set(result);
        const setResult = Array.from(set);

        const pushResult = () => {
            if (setResult.length === screenData.count) return;

            if (setResult.length < screenData.count) {
                setResult.push(mole.utils.randomNum(this.getRowXColumnsCount()))
            }    
        }

        pushResult();

        return setResult;
    }

    /** 출현한 두더지 Element List */ 
    getHolesEl = () => {
        const result = [];

        for (let i=0; i<this.getRandomHoles().length; i++) {
            result.push(document.getElementById(`${this.getRandomHoles()[i]}`));
        }

        return result;
    }

    /** 두더지 show */ 
    playingMole = (onClick?: Function) => {
        const elList = this.getHolesEl();
        console.log('elList', elList);

        // 두더지 등장
        const showMole = () => {

            elList.forEach(element => {
                // 애니메이션 추가
                mole.utils.addClassActive(element);

                console.log('element', element);
                // 클릭 이벤트 추가
                element?.addEventListener('click', (e: any) => {
                    // hit 이미지로 수정
                    console.log('click', score)
                    e.target.src = '/images/mole_hit_hole.png';

                    setTimeout(() => {
                        // 다시 이미지 복구
                        e.target.src = '/images/mole.png';
                    }, 500)

                    // 두더지 처리
                    score = score + 1;
                    catchMole(element)
                });
            });

            setTimeout(this.stoppingMole, 3000);
        }

        // 두더지 터치
        const catchMole = (element: Element) => {
            elList.forEach(element => {
                // 애니메이션 추가
                mole.utils.removeClassActive(element);

                console.log('element', element);
                // 클릭 이벤트 추가
                element?.removeEventListener('click', (e: any) => {
                    catchMole(element)
                });
            });

            clearTimeout(setTimeout(this.stoppingMole));
            clearTimeout(setTimeout(this.playingMole));
        }

        showMole();
        setTimeout(this.playingMole, 3000);
    }

    /** 두더지 hide */ 
    stoppingMole = () => {
        for (let i = 0; i<this.getRowXColumnsCount(); i++) {
            const element = document.getElementById(i.toString());

            mole.utils.removeClassActive(element);
            element?.removeEventListener('click', () => {});
        }

        // clearTimeout(setTimeout(this.playingMole))
    }

    getScore = () => {
        return score;
    }
}
