import { Store } from "redux";
import { initialDataState, initialViewState, StoreStateType } from './../type/Type';

type StateType = keyof typeof initialDataState | keyof typeof initialViewState;

export class Mole {
    private _store!: Store;

    /**
     * Store 초기화.
     * @param inStore Store
     */
    public init(inStore: Store) {
        this._store = inStore;
    }

    public get store() {
        return this._store;
    }

    public set store(inStore: Store) {
        this.store = inStore;
    }

    public get state(): StoreStateType {
        return this.store.getState()
    }

    /**
     * 스토어에 있는 상태 값 가져오기.
     * @param key DataStateType | viewStateType 키
     * @param type data | view
     * @returns 키의 값
     */
    public getState(key: StateType, type: string = 'data') {
        return this._store.getState()[type][key];
    }

}

export default new Mole();
