import mole from "../core/Mole";

const ModalRankTest = () => {
    const testData = [
        {score: 74, sysdate:'2023/02/12 20:13:20'},
        {score: 24, sysdate:'2023/05/11 15:13:20'},
        {score: 11, sysdate:'2023/02/17 21:13:20'},
        {score: 96, sysdate:'2023/02/28 05:13:20'},
        {score: 112, sysdate:'2023/02/24 16:13:20'},
        {score: 60, sysdate:'2023/06/12 12:13:20'},
        {score: 50, sysdate:'2023/05/03 21:13:20'},
        {score: 85, sysdate:'2023/09/14 23:13:20'},
        {score: 0, sysdate:'2023/01/14 11:13:20'},
        {score: 32, sysdate:'2023/08/14 06:13:20'}
    ]

    const sortRank = mole.utils.sortArray(testData, 10);

    return (
        <>
        <p>데이터를 선택하시면 테스트 데이터가 반영됩니다.</p>
        <table className="rank-table clearfix" style={{marginBottom: '10%'}}>
            <tbody>
                <tr>
                    <th>점수</th>
                    <th>일시</th>
                </tr>
                {sortRank.map((item: {
                    score: number,
                    sysdate: string
                }, idx: number) => {
                    return (
                        <tr key={item.score + '-' + item.sysdate}
                            onClick={() => {
                                if (idx === 0) {
                                    mole.setState('rankList', 
                                    [
                                        ...sortRank, 
                                        {
                                            score: mole.module.getScore(), 
                                            sysdate: mole.utils.sysdate(new Date())
                                        }
                                    ])
                                } else {
                                    mole.setState('rankList', {
                                        ...sortRank,
                                        score: item.score, 
                                        sysdate: item.sysdate
                                    })
                                }

                                mole.hideModal();
                            }}
                        >
                            <td>{idx === 0 ? '모두 선택' : `${item.score}점`}</td>
                            <td>{idx === 0 ? '모두 선택' : item.sysdate}</td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        </>
    )
}

export default ModalRankTest;
