import { initialViewState, AlertOptionType } from './../type/Type';
import { createAction } from "@reduxjs/toolkit";

// types
export const MOVE_PAGE = "view/MOVE_PAGE";
export const SET_COVER = "view/SET_COVER";
export const SHOW_ALERT = "view/SHOW_ALERT";
export const HIDE_ALERT = "view/HIDE_ALERT";

// actions
export const movePage = createAction<{
    pageName: string;
}>(MOVE_PAGE);

export const showAlert = createAction<{
    message: string;
    option: AlertOptionType;
}>(SHOW_ALERT);

export const hideAlert = createAction(SHOW_ALERT);

const viewReducer = (
    state = initialViewState, 
    action: {type: string, payload?: any}
) => {
    switch (action.type) {
        case MOVE_PAGE:
            return {
                ...state,
                pageName: action.payload.pageName
            }
        default:
            return state;
    }
}

export default viewReducer;
