import { useState } from "react";
import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";

interface listInfoType {
    label: string, 
    prevCode: string, 
    nextCode: string
}

const Game = () => {
    const [ setPage ] = useMovePage();
    const { screenData, score, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            score: state.data.score,
            timer: state.data.timer 
        }
    });

    const BtnGroup = () => {
        const list = mole.list.timerBtnList.find(e => e.value === timer)?.info;

        const btnClickHandler = (item: listInfoType) => {
            mole.setState('timer', item.nextCode);
            // 그만하기인 경우 준비화면으로 이동
            if (item.nextCode === 'stop') setPage('/')
        }
        return (
            <>
                {list?.map((item: {
                    label: string, 
                    prevCode: string, 
                    nextCode: string
                }, idx: number) => {
                    return <button type="button" 
                                    key={item + '-' + idx}
                                    name={item.prevCode}
                                    onClick={() => (btnClickHandler(item))}
                            >{item.label}</button>
                })}
            </>
        )
    }

    return (
        <>
        <ProgressBar />
        {`${score}점`}
        <BtnGroup/>
        </>
    )
}

export default Game;
