import React, { useState } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import mole from "../../core/Mole";
import styled from "styled-components";

interface SelectPropsType {
    type: 'row' | 'column' | 'count';       // 행 | 열 | 두더지 수
    list: {value: number, label: number}[]  // Select Option
}

const Select = (props: SelectPropsType) => {
    const [isOpen, setIsOpen] = useState(false); // Select 오픈 여부
    const toggling = () => setIsOpen(!isOpen);

    const { screenData, row, column, count } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            row: state.data.screenData.row,
            column: state.data.screenData.column,
            count: state.data.screenData.count
        }
    });

    const getTitle = () => {    
        let title: string = '';

        switch (props.type) {
            case 'row': title = '행'; break;
            case 'column': title = '열'; break;
            case 'count': title = '두더지'; break;
        }

        return screenData[props.type] + ' ' + title;
    }

    const onClick = (e: React.MouseEvent<HTMLLIElement>) => {
        setIsOpen(false);

        // 값 세팅
        mole.setState('screenData', {
            ...screenData, 
            [props.type]: Number(e.currentTarget.textContent)
        });

       // 행, 열이 변경돼서 선택할 수 없는 두더지 수가 되면 두더지 수 리셋
        if (row > 0 && column > 0 && props.type !== 'count') {
            const min = mole.list.moleCountList()[0]?.value;
            const max = mole.list.moleCountList()[mole.list.moleCountList().length -1]?.value;

            if (min >= count || max <= count) {
                mole.setState('screenData', {
                    ...mole.getState('screenData'), 
                    count: 1
                });
            }
        }
    }

    return (
        <Main>
            <SelectContainer>
                <SelectHeader onClick={toggling}>{getTitle()}</SelectHeader>
            {isOpen && <SelectListContainer>
                            <SelectList>
                                {props.list.map((item: {value: number, label: number}) => {
                                    return <ListItem onClick={onClick} key={item.value + '-' + item.label}>{item.label}</ListItem>
                                })}
                            </SelectList>
                        </SelectListContainer>
            }
            </SelectContainer>
        </Main>
    )
}

const Main = styled("div")`
    font-family: sans-serif;
    background: #f0f0f0;
`;

const SelectContainer = styled("div")`
    width: 10.5em;
    margin: 0 auto;
`;

const SelectHeader = styled("div")`
    margin-bottom: 0.8em;
    padding: 0.4em 2em 0.4em 1em;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
    font-weight: 500;
    font-size: 1.3rem;
    color: #3faffa;
    background: #ffffff;
`;

const SelectListContainer = styled("div")``;

const SelectList = styled("ul")`
    padding: 0;
    margin: 0;
    padding-left: 1em;
    background: #ffffff;
    border: 2px solid #e5e5e5;
    box-sizing: border-box;
    color: #3faffa;
    font-size: 1.3rem;
    font-weight: 500;
    &:first-child {
        padding-top: 0.8em;
    }
`;

const ListItem = styled("li")`
    list-style: none;
    margin-bottom: 0.8em;
`;

export default Select;
