import React, { useState, useRef } from 'react';
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import useMovePage from "../../hooks/useMovePage";
import mole from "../../core/Mole";

const ProgressBar = () => {
    const [ setPage ] = useMovePage();
    const { timer, time } = useSelector((state: StoreStateType) => {
        return {
            timer: state.data.timer,
            time: state.data.time,
        }
    });

    if ((timer === 'start')) {
        const interval = setInterval(() => {
            mole.setState('time', time - 1);

            if (time === 0) {
                // result 페이지로 이동
                setPage('/result');
                clearInterval(interval);
            }
        }, 1000);
    }

    return (
        <>
        <progress value={time} max={60}/>
        {/* <input ref={timeRef} type="text" placeholder={timeRef.current}/> */}
        <span>{time}</span>
        </>
    )
}

export default ProgressBar;
