import { useEffect } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../type/Type';
import mole from "../core/Mole";
import ButtonGroup from "../comp/module/ButtonGroup";
import Score from "../comp/module/Score";
import useMovePage from "../hooks/useMovePage";

const Rank = () => {
    const [ setPage ] = useMovePage();
    const { rankList } = useSelector((state: StoreStateType) => {
        return {
            rankList: state.data.rankList
        }
    });

    useEffect(() => {
        mole.showModal('ModalResult', {
            confirm: '확인',
            callbackFunc: () => {

            }
        });
    }, [])

    const RankTable = () => {
        const sortRank = rankList
                            .slice(0, 10)
                            .sort((a, b) => b.score - a.score);

        return (
            <table className="rank-table clearfix" >
                <tbody>
                    <tr>
                        <th>순위</th>
                        <th>점수</th>
                        <th>일시</th>
                    </tr>
                    {sortRank.map((row: any, rowIdx: number) => {
                        return (
                            <tr key={rowIdx}>
                                <td>{rowIdx + 1}</td>
                                <td>{row.score}</td>
                                <td>{row.sysdate}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    const ResetButton = () => {
        const onClick = () => {
            if (rankList.length === 0) return mole.alert('이미 초기화 되었습니다.');

            mole.alert('정말 초기화 하시겠어요?', {
                closeHandler: (res: any) => {
                    mole.setState('rankList', [])
                }
            })
        }

        return (
            <button className="button" onClick={onClick}>
                순위 초기화
            </button>
        )
    }
    return (
        <>
        <div className="main">
            <ResetButton/>
            <RankTable/>
            <Score score={mole.getState('score')}/>
            <ButtonGroup callback={(info: listInfoType) => {
                if (info.nextCode === 'start') {
                    setPage('/game');

                    // 카운트 다운 실행
                    mole.showModal('ModalCountDown');
                    setTimeout(() => {}, 4000)
                }

                if (info.nextCode === 'stop') {
                    mole.setState('timer', 'none');
                    return setPage('/') 
                }
            }}></ButtonGroup>
        </div>
        </>
    )
}

export default Rank;
