import mole from "../core/Mole";
export default class Utils {
    /**
     * 일시 포맷팅
     * @param date 포맷팅할 Date
     * @returns yyyy/mm/dd-hh:mi:ss
     */
    sysdate(date: Date): string {
        const yyyy = date.getFullYear();
        const mm = ('0' + (date.getMonth() + 1)).slice(-2);
        const dd = ('0' + date.getDate()).slice(-2);
        const hh = ('0' + date.getHours()).slice(-2); 
        const mi = ('0' + date.getMinutes()).slice(-2);
        const ss = ('0' + date.getSeconds()).slice(-2); 

        return yyyy + '/' + mm + '/' + dd + '-' + hh + ':' + mi + ':' + ss;
    }

    /**
     * 테이블 행 or 열 리스트로 생성
     * @param num 행 or 열의 값
     * @returns 개수에 따른 height값
     */
    createArray(num: number): number[] {
        return Array(num).fill(0).map((arr, i) => {
            return i;
        })
    }

    /**
     * active 클래스 추가
     * @param el 추가할 El
     */
    addClassActive(el: Element | null) {
        el?.classList.add('active');
    }

    /**
     * active 클래스 제거
     * @param el 삭제할 El
     */
    removeClassActive(el: Element | null) {
        el?.classList.remove('active');
    }

    /**
     * 랜덤 값 가져오기
     * @param max 랜덤 값의 최대값 범위
     * @returns {string} 1~max 값 
     */
    getRandomNum(max: number): number {
        // 두더지 굴의 최소값은 4 (행2, 열2)
        const min = 4;
        let preNum;
        const randomNum = Math.floor((Math.random() * (max - min)) + min);

        debugger
        if (preNum !== randomNum && randomNum > 0) {
            preNum = randomNum; 
            return randomNum;
        }

        return this.getRandomNum(mole.getState('screenData').row * mole.getState('screenData').column);
    } 
}
