import React from "react";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../../type/Type';
import mole from "../../core/Mole";

const CountDown = () => {
    const [counter, setCounter] = React.useState(3);
    const [src, setSrc] = React.useState(`/images/count-down-${counter}.png`)
    const { isCountDown } = useSelector((state: StoreStateType) => {
        return {
            isCountDown: state.view.isCountDown
        }
    });

    // Third Attempts
    React.useEffect(() => {
        const timer = setInterval(() => setCounter(counter - 1), 1000);
        if (counter === 0) return clearInterval(timer);

        return () => {
            mole.countDown(false);
            clearInterval(timer);
        }
    }, [counter]);

    return (
        <>
        {
            isCountDown &&
            <div className="main">
                <img alt={`count-down-${counter}`} width="" src={src}/>        
            </div>
        }
        </>
    );
}   

export default CountDown;
