import React, { useState, useRef, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import useMovePage from "../../hooks/useMovePage";
import useInterval from "../../hooks/useInterval";
import mole from "../../core/Mole";

const ProgressBar = () => {
    const [ setPage ] = useMovePage();
    const [ time, setTime ] = useState(60);
    const { timer, rankList } = useSelector((state: StoreStateType) => {
        return {
            timer: state.data.timer,
            rankList: state.data.rankList
        }
    });
    let timerId: any;

    function printTime() {
        setTime(time - 1);
    }

    //시계 시작 - 재귀호출로 반복실행
    function startClock() {
        printTime();
        stopClock();
        timerId = setTimeout(startClock, 1000);
    }

    //시계 중지
    function stopClock() {
        if (timerId != null) {
            clearTimeout(timerId);
        }
    }

    // 시계 초기화
    function resetClock() {
        stopClock();
    }

    useEffect(() => {
        if (timer === 'pause') {
            stopClock();
        } else if (['stop', 'none', 'end'].includes(timer)) {
            stopClock();
        } else {
            startClock();
        }
    }, [timer])


    // useInterval(() => {
    //     if (timer === 'start') {
    //         setTime(time - 1);

    //         if (time === 0) {
    //             // result 페이지로 이동
    //             mole.setState('rankList', [...rankList, {score: 90, sysdate: mole.utils.sysdate(new Date())} ])
    //             mole.setState('timer', 'end');
    //             setPage('/rank');
    //         }
    //     }
    // }, 1000);

    return (
        <>
        {
            ['start', 'pause'].includes(timer) && 
            <div className='progress-wrap'>
                <progress className='progress-bar' value={time} max={60}/>
                <span className='progress-text'>{`${time}초`}</span>
            </div>
        }
        </>
    )
}

export default React.memo(ProgressBar);
