import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import ModalCountDown from "../modal/ModalCountDown";
import ModalResult from "../modal/ModalResult";
import styled, { keyframes, css} from "styled-components";
import mole from "../core/Mole";

const Modal = () => {
    const { modalName, modalOption, isShowModal } = useSelector((state: StoreStateType) => {
        return {
            modalName: state.view.modalName,
            modalOption: state.view.modalOption,
            isShowModal: state.view.isShowModal,
        };
    });

    const loadPopup = () => { 
        switch (modalName) {
            case 'ModalCountDown':  return <ModalCountDown/>;
            case 'ModalResult':     return <ModalResult/>;
            default: return;
        }
    }

    return (
        <>
        { isShowModal && 
            <div className="modal">
                <div className="modal-body">
                    {loadPopup()}
                    {modalOption?.confirm &&<button onClick={() => (mole.hideModal())}>{modalOption?.confirm}</button>}
                </div>                
            </div>
        }
        </>

        // <div>
        //     <Background visible={isShowModal} />
        //     <ModalSection visible={isShowModal}>
        //         <Content>{loadPopup()}</Content>
        //         {modalOption?.confirm &&<button onClick={() => (mole.hideModal())}>{modalOption?.confirm}</button>}
        //     </ModalSection>
        // </div>
    );
}

// animations
const fadeIn = keyframes`
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
`;

const fadeOut = keyframes`
    0% {
        opacity: 1;
    }
    100% {
        opacity: 0;
    }
`;

// components
const modalSettings = (visible: boolean) => css`
    visibility: ${visible ? 'visible' : 'hidden'};
    z-index: 15;
    animation: ${visible ? fadeIn : fadeOut} 0.15s ease-out;
    transition: visibility 0.15s ease-out;
`;

const Background = styled.div<{ visible: boolean }>`
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    position: fixed;
    background-color: rgba(0, 0, 0, 0.6);
    ${(props) => modalSettings(props.visible)}
`;

const ModalSection = styled.div<{ visible: boolean }>`
    width: 50%;
    position: absolute;
    top: 40%;
    left: 50%;
    transform: translate(-50%, -50%);
    ${(props) => modalSettings(props.visible)}
`;

const Title = styled.div`
    display: flex;
    justify-content: flex-end;
    padding: 0 16px;
`;

const Content = styled.div`
    padding: 16px 0;

`;

export default Modal;
