import React, { useState, useRef, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import useMovePage from "../../hooks/useMovePage";
import useInterval from "../../hooks/useInterval";

const ProgressBar = () => {
    const [ setPage ] = useMovePage();
    const [ time, setTime ] = useState(60);
    const { timer } = useSelector((state: StoreStateType) => {
        return {
            timer: state.data.timer
        }
    });

    useInterval(() => {
        if (timer === 'start') {
            setTime(time - 1);

            if (time === 0) {
                // result 페이지로 이동
                setPage('/result');
            }
        }
    }, 1000);

    return (
        <>
        {
            ['start', 'pause'].includes(timer) && 
            <div className='progress-wrap'>
                <progress className='progress-bar' value={time} max={60}/>
                <span className='score-text'>{time}</span>
            </div>
        }
        </>
    )
}

export default React.memo(ProgressBar);
