import styled from "styled-components";
import { useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import mole from '../../core/Mole';
import { StoreStateType } from '../../type/Type';

// div 태그에 Modal을 구현하는데 전체적으로 필요한 CSS를 구현
export const ModalContainer = styled.div`
  // Open Modal 글자의 위치를 중간으로 이동
    height: 15rem;
    text-align: center;
    margin: 120px auto;
`;

// div 태그에 Modal이 떴을 때의(클릭 후) 배경을 깔아주는 CSS를 구현
export const ModalBackdrop = styled.div`
    background-color: greenyellow;
    text-decoration: none;
    border: none;
    padding: 20px;
    color: white;
    border-radius: 30px;
`;

// button 태그에 Modal 버튼을 보여주는 CSS를 구현
export const ModalBtn = styled.button`
    background-color: #4000c7;
    text-decoration: none;
    border: none;
    padding: 20px;
    color: white;
    border-radius: 30px;
    cursor: grab;
`;

// div 태그로서 Modal이 떴을 때 Modal 창의 CSS를 구현
  // attrs 메소드를 이용해서 아래와 같이 div 엘리먼트에 속성을 추가
export const ModalView = styled.div.attrs(props => ({ 
    role: 'dialog'
}))`
    // Modal창 CSS를 구현
    text-decoration: none;
    border: none;
    padding: 20px;
    color: white;
    border-radius: 30px;
`;

const Alert = () => {
    const refBtn = useRef<HTMLButtonElement>(null);
    const { alertMessage, alertOption, alertShowCheck } = useSelector(
        (state: StoreStateType) => {
            return {
                alertMessage: state.view.alertMessage,
                alertOption: state.view.alertOption,
                alertShowCheck: state.view.alertShowCheck
            };
        },
    );

    useEffect(() => {
        setTimeout(() => {
            refBtn.current?.focus();
        }, 100);
    }, [alertMessage]);

    return (
        <>
        <ModalContainer>
            <ModalBtn> 
            {alertShowCheck ? "Opened!" : "Open Modal"}
            </ModalBtn>
            {alertShowCheck ? (<ModalBackdrop />)&&(<ModalView />) : ""}
        </ModalContainer>
        </>
    );

}

export default Alert;
