import { useState } from "react";
import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";
import styled, { css } from 'styled-components';

interface listInfoType {
    label: string, 
    nextCode: string
}

const Game = () => {
    const [ setPage ] = useMovePage();
    const { screenData, score, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            score: state.data.score,
            timer: state.data.timer 
        }
    });


    const BtnGroup = () => {
        const list = mole.list.timerBtnList.find(e => e.value === timer)?.info;

        // 버튼 클릭 이벤트 핸들러
        const btnClickHandler = (ifno: listInfoType) => {
            mole.setState('timer', ifno.nextCode);

            // 그만하기인 경우 준비화면으로 이동
            if (ifno.nextCode === 'stop') setPage('/'); 
        }

        return (
            <div className="btn-wrap">
                {list?.map((item: {
                    label: string,
                    nextCode: string
                }, idx: number) => {
                    return (
                        <button type="button" 
                                className="game-btn"
                                key={item + '-' + idx}
                                onClick={() => (btnClickHandler(item))}
                        >{item.label}</button>)
                })}
            </div>
        )
    }

    const Score = () => {
        return (
            <div className="point-box clearfix">
                <div className="point-wrap">
                    <p className="point-text">Score :</p>
                    <p className="count-score">{`${score}점`}</p>
                </div>
            </div>
        )
    }

    const List = () => {
        return (
            <table className="mole-list clearfix" >
                {mole.utils.createArray(screenData.column).map((item: number) => {
                    return (
                        <tr style={{height: '300px'}}>
                            {mole.utils.createArray(screenData.row).map((item: number) => {
                                return (
                                    <td className="mole" key={item} >
                                        <img src="/public/images/mole.jpg"/>
                                    </td>
                                )
                            })}
                        </tr>
                    )
                })}
            </table>
        )
    }

    return (
        <div className="main">
        <ProgressBar />
        <Score/>

            <div id="ending" className="ending">
                <p id="ending-box"></p>
            </div>
            <div id="ending-bg" className="ending-bg"></div>    
            <List/>
        <BtnGroup/>
        </div>
    )
}

export default Game;
