import { useState } from "react";
import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";
import styled, { css } from 'styled-components';

interface listInfoType {
    label: string, 
    nextCode: string
}

const Game = () => {
    const [ setPage ] = useMovePage();
    const { screenData, score, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            score: state.data.score,
            timer: state.data.timer 
        }
    });


    const BtnGroup = () => {
        const list = mole.list.timerBtnList.find(e => e.value === timer)?.info;

        // 버튼 클릭 이벤트 핸들러
        const btnClickHandler = (ifno: listInfoType) => {
            mole.setState('timer', ifno.nextCode);

            // 그만하기인 경우 준비화면으로 이동
            if (ifno.nextCode === 'stop') setPage('/'); 
        }

        return (
            <div className="btn-wrap">
                {list?.map((item: {
                    label: string,
                    nextCode: string
                }, idx: number) => {
                    return (
                        <button type="button" 
                                className="game-btn"
                                key={item + '-' + idx}
                                onClick={() => (btnClickHandler(item))}
                        >{item.label}</button>)
                })}
            </div>
        )
    }

    const Score = () => {
        return (
            <div className="point-wrap">
                <p className="point-text">Score :</p>
                <p className="count-score">{score}</p>
                <p className="count-total-mole">{50}</p>
            </div>
        )
    }

    return (
        <div className="main">
        <ProgressBar />
        <Score/>

            <div id="ending" className="ending">
            <p id="ending-box"></p>
            </div>
            <div id="ending-bg" className="ending-bg"></div>    
            <ul className="whack-a-mole clearfix">
            <li className="mole">
                <img id="1" src="/public/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="2" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="3" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="4" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="5" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="6" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="7" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="8" src="assets/images/tunnel.jpg"/>
            </li>
            <li className="mole">
                <img id="9" src="assets/images/tunnel.jpg"/>
            </li>
            </ul>
            <div className="point-box clearfix">

        </div>
        <BtnGroup/>
        </div>
    )
}

export default Game;
