import React, { useState } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import mole from "../../core/Mole";
import styled from "styled-components";

interface SelectPropsType {
    type: 'row' | 'column' | 'count';
    list: {value: number, label: number}[]
}

const SelectList = (props: SelectPropsType) => {
    const [isOpen, setIsOpen] = useState(true);
    const toggling = () => setIsOpen(!isOpen);

    const getTitle = () => {
        let title: string = '';

        switch (props.type) {
            case 'row': title = '행'; break;
            case 'column': title = '열'; break;
            case 'count': title = '두더지 수'; break;
        }

        return title;
    }

    const { screenData } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData
        }
    });

    const onClick = (e: React.MouseEvent<HTMLLIElement>) => {
        setIsOpen(false);
        mole.setState('screenData', {
            ...screenData, 
            [props.type]: Number(e.currentTarget.textContent)
        });
    }
    return (
        <Main>
                <DropDownContainer>
                    <DropDownHeader onClick={toggling}>{getTitle()}</DropDownHeader>
                {isOpen && 
                    <DropDownListContainer>
                        <DropDownList>
                            {props.list.map((item: {value: number, label: number}) => {
                                return <ListItem 
                                            onClick={onClick}
                                            key={item.value + '-' + item.label}>
                                        {item.label}
                                        </ListItem>
                            })}
                        </DropDownList>
                    </DropDownListContainer>
                }
                </DropDownContainer>
        </Main>
    )
}

const Main = styled("div")`
    font-family: sans-serif;
    background: #f0f0f0;
    height: 100vh;
`;

const DropDownContainer = styled("div")`
    width: 10.5em;
    margin: 0 auto;
`;

const DropDownHeader = styled("div")`
    margin-bottom: 0.8em;
    padding: 0.4em 2em 0.4em 1em;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
    font-weight: 500;
    font-size: 1.3rem;
    color: #3faffa;
    background: #ffffff;
`;

const DropDownListContainer = styled("div")``;

const DropDownList = styled("ul")`
    padding: 0;
    margin: 0;
    padding-left: 1em;
    background: #ffffff;
    border: 2px solid #e5e5e5;
    box-sizing: border-box;
    color: #3faffa;
    font-size: 1.3rem;
    font-weight: 500;
    &:first-child {
        padding-top: 0.8em;
    }
`;

const ListItem = styled("li")`
    list-style: none;
    margin-bottom: 0.8em;
`;

export default SelectList;
