import { useEffect } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../type/Type';
import mole from "../core/Mole";
import ButtonGroup from "./../comp/module/ButtonGroup";
import Score from "./../comp/module/Score";
import useMovePage from "../hooks/useMovePage";


const Result = () => {
    const [ setPage ] = useMovePage();
    const { rankList } = useSelector((state: StoreStateType) => {
        return {
            rankList: state.data.rankList
        }
    });

    const RankTable = () => {
        const sortRank = rankList
                            .slice(0, 10)
                            .sort((a, b) => b.score - a.score);

        return (
            <table className="mole-list clearfix" >
                <tbody>
                    <tr>
                        <th>순위</th>
                        <th>점수</th>
                        <th>일시</th>
                    </tr>
                    {sortRank.map((row: any, rowIdx: number) => {
                        return (
                            <tr key={rowIdx}>
                                <td className="mole">{rowIdx + 1}</td>
                                <td className="mole">{row.score}</td>
                                <td className="mole">{row.sysdate}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    const Reset = () => {
        const onClick = () => {
            if (rankList.length === 0) return mole.alert('이미 초기화 되었습니다.');
            mole.setState('rankList', [])
        }

        return (
            <button type="button" onClick={onClick}>
                순위 초기화
            </button>
        )
    }
    return (
        <div className="main">
            <RankTable/>
            <Score score={mole.getState('score')}/>
            <Reset/>
            <ButtonGroup callback={(info: listInfoType) => {
                if (info.nextCode === 'start') {
                    mole.setState('timer', 'start');
                    setPage('/game') 
                }

                if (info.nextCode === 'stop') {
                    mole.setState('timer', 'none');
                    return setPage('/') 
                }
            }}/>
        </div>
    )
}

export default Result;
