import { useEffect } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../type/Type';
import mole from "../core/Mole";
import ButtonGroup from "../comp/module/ButtonGroup";
import ButtonReset from "../comp/module/ButtonReset";
import Score from "../comp/module/Score";
import useMovePage from "../hooks/useMovePage";


const Rank = () => {
    const [ setPage ] = useMovePage();
    const { rankList } = useSelector((state: StoreStateType) => {
        return {
            rankList: state.data.rankList
        }
    });

    useEffect(() => {
        mole.showModal('ModalResult', {
            confirm: '확인',
            callbackFunc: () => {

            }
        });
    }, [])

    const RankTable = () => {
        const sortRank = rankList
                            .slice(0, 10)
                            .sort((a, b) => b.score - a.score);

        return (
            <table className="rank-table clearfix" >
                <tbody>
                    <tr>
                        {mole.list.rankTableHeadList.map((label: string) => {
                            return <th>{label}</th>
                        })}
                    </tr>
                    {sortRank.map((row: any, rowIdx: number) => {
                        return (
                            <tr key={rowIdx}>
                                <td>{rowIdx + 1}</td>
                                <td>{row.score}</td>
                                <td>{row.sysdate}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    return (
        <>
        <div className="main">
            <ButtonReset/>
            <RankTable/>
            <Score/>
            <ButtonGroup callback={(info: listInfoType) => {
                if (info.nextCode === 'start') {
                    setPage('/game');
                    // 카운트 다운 실행
                    mole.showModal('ModalCountDown');
                    // 카운트 다운 4초(3, 2, 1, go) 종료 후 바로 시작
                    setTimeout(() => {}, 4000)
                    mole.setState('score', 0);
                    return;
                }

                if (info.nextCode === 'stop') {
                    mole.setState('timer', 'none');
                    mole.setState('score', 0);
                    return setPage('/');
                }
            }}/>
        </div>
        </>
    )
}

export default Rank;
