import React, { useState, useEffect } from "react";
import mole from "../core/Mole";

const ModalCountDown = () => {
    const [counter, setCounter] = useState(4);

    useEffect(() => {
            const timer = setInterval(() => setCounter(counter - 1), 1000);

            if (counter === 0) {
                mole.hideModal();
                clearInterval(timer);
            }

            return () => {
                clearInterval(timer);
            }
    }, [counter]);

    return (
        <>
        {
            <div className="main">
                <img alt={`count-down-${counter}`} style={{width: '50%'}}src={`/images/count-down-${counter}.png`}/>        
                {/* <span>{counter}</span> */}
            </div>
        }
        </>
    );
}   

export default ModalCountDown;
