import styled from "styled-components";
import { useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import mole from '../../core/Mole';
import { StoreStateType } from '../../type/Type';

const boxStyle = {
    position: 'absolute',
    top: '45%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 250,
    height: 200,
    bgcolor: 'background.paper',
    border: '2px solid #90CAF7',
    boxShadow: 24,
    pt: 2,
    px: 4,
    pb: 3,
};

const btnStyle = {
    position: 'absolute',
    height: 180,
    left: '70%',
};

const Alert = () => {
    const refBtn = useRef<HTMLButtonElement>(null);
    const { alertMessage, alertOption, alertShowCheck } = useSelector(
        (state: StoreStateType) => {
            return {
                alertMessage: state.view.alertMessage,
                alertOption: state.view.alertOption,
                alertShowCheck: state.view.alertShowCheck
            };
        },
    );

    useEffect(() => {
        setTimeout(() => {
            refBtn.current?.focus();
        }, 100);
    }, [alertMessage]);

    return (
        <>
        <ModalContainer>
            <ModalBtn> 
            {alertShowCheck ? "Opened!" : "Open Modal"}
            </ModalBtn>
            {alertShowCheck ? (<ModalBackdrop />)&&(<ModalView />) : ""}
        </ModalContainer>
        </>
    );

}

export default Alert;
