import { withKnobs } from '@storybook/addon-knobs'
import ModalCountDown from "../../modal/ModalCountDown";
import ModalResult from "../../modal/ModalResult";
import ModalReadyTest from "../../modal/ModalReadyTest";
import ModalGameTest from "../../modal/ModalGameTest";
import ModalRankTest from "../../modal/ModalRankTest";
import "./../common.css";
import "./../page.css";

type ModalType = {
    /** 메시지 */
    modalName: string;
    /** 버튼 이벤트 핸들러 */
    callbackFunc?: Function;
    /** 확인 버튼 명 */
    confirm?: string;
    /** ref */
    ref?: any
}

export const Modal = (manager: ModalType) => {
    const [ isShow, setIsShow ] = useState(false);

    const ModalManager = (props: ModalType) => {
        const loadPopup = () => { 
            switch (props.modalName) {
                case 'ModalCountDown':  return <ModalCountDown/>;
                case 'ModalResult':     return <ModalResult/>;
                case 'ModalReadyTest':  return <ModalReadyTest/>;
                case 'ModalGameTest':   return <ModalGameTest/>;
                case 'ModalRankTest':   return <ModalRankTest/>;
                default: return;
            }
        }

        const btnClickHandler = () => {
            props.callbackFunc && props.callbackFunc();
            setIsShow(false);
        }

        return (
            <>
            { isShow && 
            <div className="modal">
                <div className={"modal-body"}>
                    {loadPopup()}
                    {props?.confirm && 
                        <button className="button" 
                                onClick={btnClickHandler}
                                ref={props.ref}
                        >{props?.confirm}</button>
                    }
                    <p onClick={() => (setIsShow(false))}>닫기</p>
                </div>                
            </div>
            }
            </>
        )
    }

    return (
        <div>
            <button className="btn-reset" onClick={() => (setIsShow(true))}>
                Modal
            </button>
            <ModalManager 
                modalName={manager.modalName} 
                confirm={manager.confirm}
                ref={manager.ref}
                callbackFunc={manager.callbackFunc}/>
        </div>
    );

}

export default {
    component: Modal,
    title: 'Core/Modal',
    decorators: [withKnobs]   
}";
