import { useRef } from "react";

const CountDown = () => {
    const numsRef = useRef<any>([]);
    const counter = document.querySelector('.counter');
    const finalMessage = document.querySelector('.final');
    const repl = document.getElementById('replay');

    runAnimation();

    function resetDOM() {
        counter?.classList.remove('hide');
        finalMessage?.classList.remove('show');

        numsRef.current.forEach((num: any) => {
            num.classList.value = '';
        });

        numsRef.current[0].classList.add('in');
    }

    function runAnimation() {
        numsRef.current.forEach((num: Element, idx) => {
            const penultimate = nums.length - 1;
            num.addEventListener('animationend', (e: any) => {
                if(e.animationName === 'goIn' && idx !== penultimate){
                    num.classList.remove('in');
                    num.classList.add('out');
                } else if (e.animationName === 'goOut' && num.nextElementSibling){
                    num.nextElementSibling.classList.add('in');
                } else {
                    counter?.classList.add('hide');
                    finalMessage?.classList.add('show');
                }
            });
        });
    }

    repl?.addEventListener('click', () => {
        resetDOM();
        runAnimation();
    });

    return (
        <>
        <div className="counter">
            <div className="nums">
                <span className="in">3</span>
                <span ref={el => numsRef.current[0] = el}>2</span>
                <span ref={el => numsRef.current[1] = el}>1</span>
                <span ref={el => numsRef.current[1] = el}>0</span>
            </div>
            <h4>Get Ready</h4>
            </div>
            <div className="final">
            <h1>GO</h1>
            <button id="replay">replay</button>
        </div>
        </>
    )
}

export default CountDown;
