import mole from "./Mole";

export default class Module {
    getRowXColumnsCount = () => {
        const screenData = mole.getState('screenData');
        return screenData.row * screenData.column;
    }

    getRandomHoles = () => {
        const screenData = mole.getState('screenData');
        const result = []

        for (let i = 0; i<screenData.count; i++) {
            result.push(mole.utils.randomNum(screenData.row * screenData.column));
        }

        return result;
    }

    /** 출현한 두더지 Element List */ 
    getHolesEl = () => {
        const result = [];

        for (let i=0; i<this.getRandomHoles().length; i++) {
            result.push(document.getElementById(`${this.getRandomHoles()[i]}`));
        }

        return result;
    }

    playingMole = (onClick?: Function) => {
        const showMole = () => {
            const elList = this.getHolesEl();

            elList.forEach(element => {
                // 애니메이션 추가
                mole.utils.addClassActive(element);

                // 클릭 이벤트 추가
                element?.addEventListener('click', (e: any) => {
                    // hit 이미지로 수정
                    e.target.src = '/images/mole_hit_hole.png';

                    setTimeout(() => {
                        // 다시 이미지 복구
                        e.target.src = '/images/mole.png';
                    }, 500)

                    // 두더지 처리
                    catchMole(element)
                });
            });

            setTimeout(this.stoppingMole, 2000);
        }

        // 두더지 터치
        const catchMole = (element: Element) => {
            mole.utils.removeClassActive(element);
            element?.removeEventListener('click', (e: any) => {
                e.target.src = '/images/mole.png';
            });

            clearTimeout(setTimeout(this.playingMole));
        }

        showMole();
        setTimeout(this.playingMole, 2000);
    }

    stoppingMole = () => {
        for (let i = 0; i<this.getRowXColumnsCount(); i++) {
            const element = document.getElementById(i.toString());

            mole.utils.removeClassActive(element);
            element?.removeEventListener('click', () => {});
        }

        // clearTimeout(this.playingMole().)
    }
}
