import { initialViewState } from './../type/Type';
import { createAction } from "@reduxjs/toolkit";

// types
export const MOVE_PAGE = "view/MOVE_PAGE";
export const SET_COVER = "view/SET_COVER";

// actions
export const movePage = createAction<{
    pageName: string;
}>(MOVE_PAGE)

export const setCover = createAction<{
    coverState: 'loading' | 'show' 
}>(SET_COVER)

const viewReducer = (
    state = initialViewState, 
    action: {type: string, payload?: any}
) => {
    switch (action.type) {
        case MOVE_PAGE:
            return {
                ...state,
                pageName: action.payload.pageName
            }
        case SET_COVER:
            return {
                ...state,
                coverState: 'show'
            }
        default:
            return state;
    }
}

export default viewReducer;
