import React, { useEffect, useState } from 'react';

interface ProgressBarPropsType {
    maxTime: number;
}


const ProgressBar = (props: ProgressBarPropsType) => {
    const [ time, setTime ] = useState(props.maxTime);

    useEffect(() => {
        setInterval(() => setTime(time => time), 1000);

    }, [])

    return (
        <>
        <progress value={time} max={props.maxTime}/>
        <span>{time}</span>
        </>
    )
}

export default ProgressBar;
