import styled, { css } from 'styled-components';
import mole from "./../core/Mole";
import useMovePage from "../hooks/useMovePage";
import Select from "../comp/module/Select";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';

const Ready = () => {
    const [ setPage ] = useMovePage();
    const { screenData } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData
        }
    });

    // 두더지 수 드롭다운 리스트
    const countList = () => {
        const maxCount = (screenData.row * screenData.column) / 2;
        const list: {value: number, label: number}[] = [];

        // 최소 1, 최대 (행 x 열) / 2
        for (let i=1; i<maxCount; i++) {
            list.push({value: i, label: i})
        }

        return list;
    }

    return (
        <Container>
            <Select type="row" list={mole.list.rowColumn}/>
            <Select type="column" list={mole.list.rowColumn}/>
            <Select type="count" list={countList()}/>
            <Button onClick={() => (setPage('game'))}>시작</Button>
        </Container>
    )
}

const Container = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #bdc3c7;
`;

const Button = styled.button`
    display: inline-flex;
    align-items: center;
    outline: none;
    border: none;
    border-radius: 4px;
    color: white;
    font-weight: bold;
    cursor: pointer;
    padding-left: 1rem;
    padding-right: 1rem;
    height: 5.25rem;
    width: 5.8rem;
    font-size: 2rem;
    background: #228be6;
    &:hover{
        background: #339af0;
    }
    &:active{
        background: #1c7ed6;
    }
    & + & {
        margin-left: 1rem;
    }
`;

export default Ready;
