import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";
import Score from "./../comp/module/Score";
import ButtonGroup from "./../comp/module/ButtonGroup";

interface listInfoType {
    label: string, 
    nextCode: string
}

const Game = () => {
    const [ setPage ] = useMovePage();
    const { screenData, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            timer: state.data.timer
        }
    });

    const Table = () => {
        let i = -1;
        return (
            <table className="mole-list clearfix" >
                <tbody>
                    {mole.utils.createArray(screenData.column).map((columnHeight: number, columnIdx: number) => {
                        return (
                            <tr key={columnIdx}>
                                {mole.utils.createArray(screenData.row).map((row: number, rowIdx: number) => {
                                    i = i + 1;
                                    return (
                                        <td className="mole" 
                                            key={columnIdx + '-' + rowIdx} 
                                            style={{height: `${columnHeight}px`}}
                                        >
                                            <img alt="두더지 이미지" 
                                                id={i.toString()}
                                                src="images/mole.png"
                                                />
                                        </td>
                                    )
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    let score = 0;

    const getRandomHoles = () => {
        const result = []

        for (let i = 0; i<screenData.count; i++) {
            result.push(mole.utils.randomNum(screenData.row * screenData.column));
        }

        return result;
    }

    const getHolesID = () => {
        const result = [];

        for (let i=0; i<getRandomHoles().length; i++) {
            result.push(document.getElementById(`${getRandomHoles()[i]}`));
        }

        return result;
    }

    const playingMole = () => {
        const data = getHolesID();
        // 두더지 등장
        const showMole = (callback?: Function) => {
            data.forEach(element => {
                // 애니메이션 추가
                mole.utils.addClassActive(element);
                // 클릭 이벤트 추가
                element?.addEventListener('click', () => {
                    score = score + 1;
                    catchMole(element && element)
                });
            });

            setTimeout(overTimeMole, 2000);
        }

        // 두더지 터치
        const catchMole = (element: Element) => {
            mole.utils.removeClassActive(element);
            element?.removeEventListener('click', () => {});

            clearTimeout(setTimeout(playingMole, 2000));
        }

        // 시간 초과
        const overTimeMole =() => {

            data.forEach((element: any) => {
                catchMole(element)
            })

            // test중
            // 게임 모드에 따라 구분
        }

        showMole();
        setTimeout(playingMole, 2000);
    }

    const BtnGroup = () => {
        const list = mole.list.gameBtnList.find(e => e.value === timer);



        // 버튼 클릭 이벤트 핸들러
        const btnClickHandler = (info: listInfoType) => {
            mole.setState('timer', info.nextCode);

            if (!['pause', 'stop'].includes(info.nextCode)) {
                playingMole()
            } else if (['start','pause'].includes(info.nextCode)) {
                clearTimeout(setTimeout(playingMole, 1000));
            }

            // 그만하기인 경우 준비화면으로 이동
            if (info.nextCode === 'stop') setPage('/'); 
        }

        return (
            <div className="btn-wrap">
                {list?.info?.map((item: listInfoType, idx: number) => {
                    return (
                        <button type="button" 
                                className="game-btn"
                                key={item + '-' + idx}
                                onClick={() => (btnClickHandler(item))}
                        >{item.label}</button>
                    )
                })}
            </div>
        )
    }

    console.log('game', score);
    return (
        <div className="main">
            <ProgressBar />
            <Score score={score}/>
            <Table/>
            <ButtonGroup callback={(info: listInfoType) => {
                if (!['pause', 'stop'].includes(info.nextCode)) {
                    playingMole()
                } else if (['start','pause'].includes(info.nextCode)) {
                    clearTimeout(setTimeout(playingMole, 1000));
                }

                // 그만하기인 경우 준비화면으로 이동
                if (info.nextCode === 'stop') setPage('/'); 
            }}/>
            <BtnGroup/>
        </div>
    )
}

export default Game;
