import { useEffect } from "react";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../type/Type';
import mole from "./../core/Mole";
import ProgressBar from "../comp/module/ProgressBar";
import useMovePage from "../hooks/useMovePage";
import ButtonGroup from "./../comp/module/ButtonGroup";

const Game = () => {
    const [ setPage ] = useMovePage();
    const { screenData } = useSelector((state: StoreStateType) => {
        return { screenData: state.data.screenData }
    });

    useEffect(() => {
        mole.showModal('ModalCountDown');

        // 카운트 다운 종료 후 실행
        const id = setTimeout(() => {
            mole.setState('isStart', true);
            mole.module.playingMole();
        }, 4000)

        return () => {
            clearTimeout(id);
        }
    }, [])

    const btnClickHandler = (info: listInfoType) => {
        switch(info.nextCode) {
            case 'start':
                mole.showModal('ModalCountDown');

                // 카운트 다운 종료 후 실행
                setTimeout(() => {
                    mole.module.playingMole();
                }, 4000)
                break;

            case 'pause':
                mole.setState('timer', info.nextCode);
                mole.module.stopMole();
                break;

            case 'stop':
                mole.module.stopMole();
                mole.setState('isStart', false);

                mole.alert('정말 그만하시겠어요?', {
                    confirm: '네',
                    cancel: '아니요',
                    closeHandler: (res: any) => {
                        if (res === 'confirm') {
                            mole.setState('timer', 'none');
                            mole.module.setScore();
                            setPage('/'); 
                        } else {
                            mole.showModal('ModalCountDown');

                            // 카운트 다운 종료 후 실행
                            setTimeout(() => {
                                mole.setState('isStart', true);
                                mole.module.playingMole();
                            }, 4000)
                        }
                    }
                })
                break;
        }
    }

    const Table = () => {
        // 두더지 id
        let i = -1;

        return (
            <table className="mole-list clearfix" >
                <tbody>
                    {mole.utils.createArray(screenData.column).map((column: number, columnIdx: number) => {
                        return (
                            <tr key={columnIdx}>
                                {mole.utils.createArray(screenData.row).map((row: number, rowIdx: number) => {
                                    i = i + 1;
                                    return (
                                        <td className="mole" 
                                            key={columnIdx + '-' + rowIdx} 
                                        >
                                            <img alt="두더지 이미지" 
                                                id={i.toString()}
                                                src="images/mole.png"
                                                />
                                        </td>
                                    )
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    return (
        <div className="main">
            <button className='btn-test' onClick={() => (mole.showModal('ModalGameTest', {confirm: '닫기'}))}>Test</button>
            <ProgressBar />
            <Table/>
            <ButtonGroup callback={(info: listInfoType) => (btnClickHandler(info))}/>
        </div>
    )
}

export default Game;
