import React from "react";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import mole from "../core/Mole";

const ModalCountDown = () => {
    const [counter, setCounter] = React.useState(4);

    console.log(counter);
    // Third Attempts
    React.useEffect(() => {
            const timer = setInterval(() => setCounter(counter - 1), 1000);

            if (counter === 0) {
                mole.hideModal();
                clearInterval(timer);
            }

            return () => {
                clearInterval(timer);
            }
    }, [counter]);

    return (
        <>
        {
            <div className="main">
                <span>{counter}</span>
                {/* <img alt={`count-down-${counter}`} width="" src={`/images/count-down-${counter}.png`}/>         */}
            </div>
        }
        </>
    );
}   

export default ModalCountDown;
