import React, { useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import useMovePage from "../../hooks/useMovePage";

const ProgressBar = () => {
    const [ setPage ] = useMovePage();
    const timeRef = useRef<any>(60);
    const { timer } = useSelector((state: StoreStateType) => {
        return {
            timer: state.data.timer
        }
    });

    useEffect(() => {
        if ((timer === 'start')) {
            const interval = setInterval(() => {
                timeRef.current = timeRef.current - 1;

                if (timeRef.current === 0) {
                    // result 페이지로 이동
                    setPage('/result');
                    clearInterval(interval);
                }
            }, 1000);
        }
    }, [timer]);

    return (
        <>
        <progress value={timeRef.current} max={60}/>
        <input ref={timeRef} type="text" placeholder="id">timeRef.current</input>
        {/* <span ref={timeRef}>{timeRef.current}</span> */}
        </>
    )
}

export default ProgressBar;
