import React from "react";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../../type/Type';
import mole from "../../core/Mole";

const CountDown = () => {
    const [counter, setCounter] = React.useState(4);
    const { isCountDown } = useSelector((state: StoreStateType) => {
        return {
            isCountDown: state.view.isCountDown
        }
    });

    console.log(counter);
    // Third Attempts
    React.useEffect(() => {
            const timer = setInterval(() => setCounter(counter - 1), 1000);

            if (counter === 0) {
                mole.countDown(false);
                clearInterval(timer);
            }

            return () => {
                clearInterval(timer);
            }
    }, [counter]);

    return (
        <>
        {
            isCountDown &&
            <div className="main">
                <span>{counter}</span>
                {/* <img alt={`count-down-${counter}`} width="" src={`/images/count-down-${counter}.png`}/>         */}
            </div>
        }
        </>
    );
}   

export default CountDown;
