import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType, listInfoType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";
import ButtonGroup from "./../comp/module/ButtonGroup";
import { useRef } from "react";

const Game = () => {
    const scoreRef = useRef<any>(0);
    const [ setPage ] = useMovePage();
    const { screenData, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            timer: state.data.timer
        }
    });

    const Table = () => {
        let i = -1;
        return (
            <table className="mole-list clearfix" >
                <tbody>
                    {mole.utils.createArray(screenData.column).map((column: number, columnIdx: number) => {
                        return (
                            <tr key={columnIdx}>
                                {mole.utils.createArray(screenData.row).map((row: number, rowIdx: number) => {
                                    i = i + 1;
                                    return (
                                        <td className="mole" 
                                            key={columnIdx + '-' + rowIdx} 
                                        >
                                            <img alt="두더지 이미지" 
                                                id={i.toString()}
                                                src="images/mole.png"
                                                />
                                        </td>
                                    )
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    let score = 0;

    const getRandomHoles = () => {
        const result = []

        for (let i = 0; i<screenData.count; i++) {
            result.push(mole.utils.randomNum(screenData.row * screenData.column));
        }

        return result;
    }

    const getHolesID = () => {
        const result = [];

        for (let i=0; i<getRandomHoles().length; i++) {
            result.push(document.getElementById(`${getRandomHoles()[i]}`));
        }

        return result;
    }

    const timerId = setTimeout(overTimeMole, 2000);

    const playingMole = () => {
        const data = getHolesID();

        // 두더지 등장
        const showMole = (callback?: Function) => {
            data.forEach(element => {
                // 애니메이션 추가
                mole.utils.addClassActive(element);
                // 클릭 이벤트 추가
                element?.addEventListener('click', () => {
                    score = score + 1;
                    catchMole(element && element)
                });
            });

            setTimeout(overTimeMole, 2000);
        }

        // 두더지 터치
        const catchMole = (element: Element) => {
            mole.utils.removeClassActive(element);
            element?.removeEventListener('click', () => {});

            clearTimeout(setTimeout(playingMole));
        }

        // 시간 초과
        const overTimeMole =() => {

            data.forEach((element: any) => {
                catchMole(element)
            })

            // test중
            // 게임 모드에 따라 구분
        }



        showMole();
        setTimeout(playingMole, 2000);

    }

    return (
        <div className="main">
            <ProgressBar />
            <div className="score-box">
                <div className="score-wrap">
                    <p className="score-text">Score :</p>
                    <p className="count-score" ref={scoreRef}>{score}</p>
                </div>
            </div>
            <Table/>
            <ButtonGroup callback={(info: listInfoType) => {
                if (!['pause', 'stop'].includes(info.nextCode)) {
                    mole.setState('timer', 'start');
                    playingMole()
                    // mole.showModal('ModalCountDown');
                    // // 카운트 다운 종료 후 실행
                    // setTimeout(() => {
                    //     playingMole()
                    // }, 4000)
                } else if (['start','pause'].includes(info.nextCode)) {
                    mole.setState('timer', info.nextCode);

                    for (let i = 0; i<screenData.row * screenData.column; i++) {
                        mole.utils.removeClassActive(document.getElementById(i.toString()));
                    }

                    clearTimeout(setTimeout(playingMole));
                }

                // 그만하기인 경우 준비화면으로 이동
                if (info.nextCode === 'stop') {
                    mole.setState('timer', 'none');
                    setPage('/'); 
                }
            }}/>
        </div>
    )
}

export default Game;
