import ProgressBar from "../comp/module/ProgressBar";
import { useSelector } from 'react-redux';
import { StoreStateType } from '../type/Type';
import useMovePage from "../hooks/useMovePage";
import mole from "./../core/Mole";
import { useRef } from "react";

interface listInfoType {
    label: string, 
    nextCode: string
}

const Game = () => {
    const tdRef = useRef<any>([]);
    const [ setPage ] = useMovePage();
    const { screenData, score, timer } = useSelector((state: StoreStateType) => {
        return {
            screenData: state.data.screenData,
            score: state.data.score,
            timer: state.data.timer 
        }
    });

    const Table = () => {
        let i = -1;
        return (
            <table className="mole-list clearfix" >
                <tbody>
                    {mole.utils.createArray(screenData.column).map((columnHeight: number, columnIdx: number) => {
                        return (
                            <tr key={columnIdx}>
                                {mole.utils.createArray(screenData.row).map((row: number, rowIdx: number) => {
                                    i = i + 1;
                                    return (
                                        <td className="mole" 
                                            id={i.toString()}
                                            key={columnIdx + '-' + rowIdx} 
                                            style={{height: `${columnHeight}px`}}
                                        >
                                            <img alt="두더지 이미지" src="/public/images/mole.png"/>
                                        </td>
                                    )
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        )
    }

    const BtnGroup = () => {
        const list = mole.list.timerBtnList.find(e => e.value === timer);

        // 버튼 클릭 이벤트 핸들러
        const btnClickHandler = (ifno: any) => {
            debugger
            mole.setState('timer', ifno.nextCode);
            if (ifno.nextCode === 'start') showingMole();

            // 그만하기인 경우 준비화면으로 이동
            if (ifno.nextCode === 'stop') setPage('/'); 
        }

        return (
            <div className="btn-wrap">
                {list?.info?.map((item: listInfoType, idx: number) => {
                    return (
                        <button type="button" 
                                className="game-btn"
                                key={item + '-' + idx}
                                onClick={() => (btnClickHandler(item))}
                        >{item.label}</button>
                    )
                })}
            </div>
        )
    }

    const Score = () => {
        return (
            <div className="score-box">
                <div className="score-wrap">
                    <p className="score-text">Score :</p>
                    <p className="count-score">{`${15}`}</p>
                </div>
            </div>
        )
    }

    const showingMole = () => {
        const randomHole = mole.utils.randomNum(screenData.row * screenData.column);

        const moleNumber = document.getElementById(`${randomHole}`);
        console.log(moleNumber);
        mole.utils.showMole(moleNumber);
        moleNumber?.addEventListener('click', catchMole);
        const moleCatch = setTimeout(seeMOle, 3000);

        var cntBox = document.querySelector('#count-mole');
        function seeMOle(){
            moleNumber?.removeEventListener('click', catchMole);
            mole.utils.hideMole(moleNumber);
            clearTimeout(moleCatch);
            setTimeout(showingMole, 1000);
        }

        function catchMole(){
            seeMOle();
            clearTimeout(moleCatch);
            mole.setState('score', score + 1);
        }
    }
    return (
        <div className="main">
            <Score/>
            <Table/>
            <BtnGroup/>
            <ProgressBar />
        </div>
    )
}

export default Game;
