import mole from "./Mole";

export default class List {
    // 행, 열 드롭다운 리스트
    rowColumn: {value: number, label: number}[] = [
        {value: 2, label: 2},
        {value: 3, label: 3},
        {value: 4, label: 4},
        {value: 5, label: 5},
        {value: 6, label: 6},
    ]

    // 두더지 수 리스트 가져오기
    moleCountList() {
        const screenData = mole.getState('screenData');
        const maxCount = (screenData.row * screenData.column) / 2;
        const list: {value: number, label: number}[] = [];

        // 행/열 선택 안한 케이스라면 종료
        if (maxCount === 0) return [];

        // 최소 1, 최대 (행 x 열) / 2
        for (let i=1; i<maxCount; i++) {
            list.push({value: i, label: i})
        }

        return list;
    }

    screenData: {value: string, label: string}[] = [
        {value: 'row', label: '행'},
        {value: 'column', label: '열'},
        {value: 'count', label: '두더지 수'},
    ]
}
